import axios from "axios";
import React, { useState } from "react";

import "./signin.css";
import {
  View,
  Image,
  Text,
  StyleSheet,
  Linking,
  Dimensions,
  ScrollView,
  SafeAreaView,
  TouchableOpacity,
} from "react-native";
var windowHeight = Dimensions.get("window").height;
var windowWidth = Dimensions.get("window").width;
function Login() {
  const [data, setData] = useState({ Email: "", Password: "" });
  const signin = async () => {
    console.log(data);
    await axios
      .post(`http://localhost:8080/users/login`, data)
      .then(async (response) => {
        await localStorage.setItem("token", response.data.token);
        await localStorage.setItem("userID", response.data.user_id);
        if (response.data.address.length != 0) {
          await localStorage.setItem(
            "home",
            response.data.address[0].house_name
          );
          await localStorage.setItem(
            "street",
            response.data.address[0].street_name
          );
          await localStorage.setItem(
            "city",
            response.data.address[0].city_name
          );
          await localStorage.setItem(
            "pincode",
            response.data.address[0].pin_code
          );
        }
        console.log(response);
        if (response.status === 200) {
          window.location.href = "/productview";
        } else {
          window.location.href = "/";
        }
      });
  };
  const regPage = () => {
    window.location.href = "/register";
  };

  const signInPage = () => {
    window.location.href = "/signin";
  };

  const landingPage = () => {
    window.location.href = "/";
  };

  return (
    <div className="mainDiv">
      <div className="title">
        <div onClick={landingPage}>Hollamart</div>
        <div className="navSignIn" onClick={signInPage}>
          Sign In
        </div>
        <div className="navSignUp" onClick={regPage}>
          Sign Up
        </div>
      </div>

      <div>
        {" "}
        <div className="second-part">
          <div className="login-part">
            <label
              style={{
                padding: 10,
              }}
              htmlFor="email"
              className="foremail"
            >
              Email
            </label>
            <TouchableOpacity
              style={[
                myStyles.inputField,
                { marginTop: windowHeight * 0.03, backgroundColor: "#e30914" },
              ]}
              onPress={formikProps.handleSubmit}
            >
              <Text style={myStyles.loginText}>CONTINUE</Text>
            </TouchableOpacity>
            <div className="login-one">
              <input
                id="email"
                className="foremailinp"
                name="Email"
                placeholder="Email here"
                onChange={(e) => {
                  setData({ ...data, [e.target.name]: e.target.value });
                }}
              />
            </div>
            <br />
            <label
              style={{
                padding: 10,
              }}
              htmlFor="password"
              className="forpassword"
            >
              Password
            </label>
            <div className="login-two">
              <input
                id="password"
                type="password"
                className="forpass"
                name="Password"
                placeholder="Password here"
                onChange={(e) => {
                  setData({ ...data, [e.target.name]: e.target.value });
                }}
              />
            </div>
            <br />
            <div className="login-three">
              <button className="forsignin-btn" onClick={signin}>
                Sign In
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
