import axios from "axios";
import React, { useState } from "react";
import "./Register.css";

function Register() {
  const [data, setData] = useState({ Email: "", Password: "" });

  const [userDetails, setUserDetails] = useState({
    First_Name: "",
    Last_Name: "",
    Password: "",
    Email: "",
    Phone: "",
  });
  const registerUser = async () => {
    await axios
      .post(`http://localhost:8080/users/signup`, userDetails)
      .then((res) => {
        console.log(res);
        if (res.status === 201 || res.status === 200) {
          window.alert("Registration successfull");
          window.location.href = "/";
        } else {
          window.alert(
            "Registration unsuccessful. User already exits/some input fields missing"
          );
        }
      });
  };
  const logUser = () => {
    window.location.href = "/";
  };
  return (
    <div>
      {" "}
      <div className="login-part">
        <div className="login-one">
          <label htmlFor="email" className="foremail">
            Email
          </label>
          <input
            id="email"
            className="foremailinp"
            name="Email"
            placeholder="Email here"
            onChange={(e) => {
              setData({ ...data, [e.target.name]: e.target.value });
            }}
          />
        </div>
        <br />
        <div className="login-two">
          <label htmlFor="password" className="forpassword">
            Password
          </label>
          <input
            id="password"
            type="password"
            className="forpass"
            name="Password"
            placeholder="Password here"
            onChange={(e) => {
              setData({ ...data, [e.target.name]: e.target.value });
            }}
          />
        </div>
        <br />
        <div className="login-three">
          <button className="forsignin-btn" onClick={signin}>
            Sign In
          </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
