import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Productview.css";
import Eachprod from "./Eachprod";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCartShopping } from "@fortawesome/free-solid-svg-icons";

function Productview() {
  const userid = localStorage.getItem("userID")
  const [products, setProducts] = useState([]);
  const [prodLength, setProdLen] = useState(0);
  // const currToken = localStorage.getItem("token")
  const routeToCartList = () =>{
    window.location.href = '/cartlist'
  }
  const logoutUser = () =>{
    localStorage.clear()
    window.location.href = '/'
    
  }
  useEffect(() => {
    axios.get("http://localhost:8080/users/viewproducts").then((res) => {
      console.log(res);
      setProducts(res.data);
      setProdLen(res.data.length);
    });
  }, []);
  return (
    <div>
    {prodLength && 
    <div className="gotocart">

      <div className="availableprod">Hollamart</div>
      <div>
      <FontAwesomeIcon icon={['fab', 'apple']} />
      <FontAwesomeIcon icon={['fab', 'microsoft']} />
      <FontAwesomeIcon icon={['fab', 'google']} />
      <FontAwesomeIcon icon={faCartShopping} />

      With Coffee Checked, these companies always know their coffee is hot and ready!
          <div>
    <FontAwesomeIcon icon="fa-solid fa-cart-shopping" />
  </div>
    </div>      <button className="forgotocartbtn" onClick={logoutUser}>Logout</button></div>}
      {prodLength &&      
        products.map((product) => {
          return (
              <Eachprod {...product} />
          );
        })}
    </div>
  );
}

export default Productview;
